jQuery.fn.moveParallax=function(e){var t=$.extend({velX:.001,velY:.001,offsetX:1,offsetY:1},e);return this.each(function(){var e=parseInt($(this).css("marginTop"),10),a=parseInt($(this).css("marginLeft"),10),n=$(this);t.velX=.4*Math.random()+-.2,t.velY=.4*Math.random()- -.2,t.offsetX=1*Math.random()+-.5,t.offsetY=1*Math.random()- -.5,$(this).parent().on({mousemove:function(s){var o=s.pageX,f=s.pageY,r=o*t.velX+e+t.offsetX,i=f*t.velY+a+t.offsetY;n.css({marginLeft:i,marginTop:r})}})})};